import React,{useEffect,useState} from 'react'
import axios from 'axios'
import {useParams} from 'react-router-dom'
import {Link} from 'react-router-dom';
import Dashboard from './Dashboard';
import UserDashboard from './UserDashboard';


function CartItems() {
    const token=localStorage.getItem('token');
    axios.interceptors.request.use(
        config=> {
            config.headers.authorization=`Bearer ${token}`;
            return config;
        },
        error=> {
            return Promise.reject(error);
        }

    );
    const [data,setData]=useState([])
    const [sum,setSum]=useState(0)
    useEffect(()=> {
        axios.get("http://localhost:8080/fetch/userproducts")
        .then(res=> {
            setData(res.data)
            getTotal();
        })
        .catch(err=>console.log(err));
    },[])
    const getTotal=()=> {
        axios.get("http://localhost:8080/fetch/getcartsum")
        .then(resp=>setSum(resp.data))
        .catch(err=>console.log(err));
    }
    const removeItem=(id)=> {
        axios.delete('http://localhost:8080/fetch/removefromcart/'+id)
        alert("Product Removed from Cart Succesfully");
    }
  return (
    <>
    <UserDashboard/>
    <div className='d-flex w-100 vh-100 justify-content-center align-items-center'>
        <div className='w-50 border bg-secondary text-white p-5'>
        <div>
            <h2 className='cart'>Your Total Cart Price is {sum}</h2>
        </div>
            <table className='table'>
                <thead>
                    <tr>
                        <th>Product ID</th>
                        <th>Product Name</th>
                        <th>Product Price</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((product,index)=> {
                            return <tr key={index}>
                                <td>{product.pid}</td>
                                <td>{product.pname}</td>
                                <td>{product.pprice}</td>
                                <td><button className='text-decoration-none btn btn-sm btn-danger' onClick={()=>removeItem(product.pid)}>Remove</button></td>
                                
                            </tr>
                        })
                    }
                </tbody>
            </table>
        </div>
    </div>
    </>
  )
}

export default CartItems
