import React, {useEffect,useState} from 'react'
import axios from 'axios'
import {useParams} from 'react-router-dom'

function Update() {
    const {id} =useParams();
    const [values,setValues]=useState({
        id:id,
        name:'',
        price:''
    })
    useEffect(()=> {
        axios.get('http://localhost:8080/products/fetch/'+id)
        .then(res => {
            setValues({...values,name:res.data.name,price:res.data.price})
        })
        .catch(err=> console.log(err))
    },[])


  return (
    <div className='d-flex w-100 vh-100 justify-content-center align-items-center'>
        <div className='w-50 border bg-secondary text-white p-5'>
            <form>
                <div>
                    <label htmlFor="name">Product Name:</label>
                    <input type="text" name='name' className='form-control' placeholder='Enter Product Name' value={values.name}/>
                </div>
                <div>
                    <label htmlFor="price">Product Price:</label>
                    <input type="text" name='price' className='form-control' placeholder='Enter Product Price' value={values.price} />
                </div>
                <button className='btn btn-info'>Update</button>
            </form>
        </div>
        
    </div>
  )
}

export default Update
