import React,{useEffect,useState} from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom';

function ViewBuydata() {
    const token=localStorage.getItem('token');
    axios.interceptors.request.use(
        config=> {
            config.headers.authorization=`Bearer ${token}`;
            return config;
        },
        error=> {
            return Promise.reject(error);
        }

    );

    const [data,setData]=useState([])
    const [response,setResponse]=useState()

    useEffect(()=> {
        axios.get("http://localhost:8080/fetch/getbuyitems")
        .then(res=>setData(res.data))
        .catch(err=>console.log(err));
    },[])
    const onReplace=(bdate)=> {
        console.log(bdate);
        console.log(bdate);

        axios.get('http://localhost:8080/fetch/replace/'+bdate)
        .then(res=>{
            console.log(res.data);
            setResponse(res.data);
            alert(response); 
        }) 
        .catch(err=>console.log(err));
    }
  return (
    <div className='d-flex w-100 vh-100 justify-content-center align-items-center'>
        <div className='w-50 border bg-secondary text-white p-5'>
            <table className='table'>
                <thead>
                    <tr>
                        <th>Product ID</th>
                        <th>Products Ordered</th>
                        <th>Booking Date</th>
                        <th>Product Details</th>
                        <th>Replace</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((product,index)=> {
                            return <tr key={index}>
                                <td>{product.bid}</td>
                                <td>{product.requiredProducts}</td>
                                <td>{product.bdate}</td>
                                <td>
                                    <div>Id:{product.cartData.cdid}</div><br/>
                                    <div>Name:{product.cartData.cdid}</div><br/>
                                    <div>Price:{product.cartData.cdprice}</div>

                                </td>
                                <td><button className='text-decoration-none btn btn-sm btn-warning' onClick={()=>onReplace(product.bdate)}>Replace</button></td>
                            </tr>
                        })
                    }
                </tbody>
            </table>
        </div>
    </div>
  )
}

export default ViewBuydata
