import React,{useEffect,useState} from 'react'
import axios from 'axios'

function UpdateProduct() {
    const[user,setUser]=useState([])
    const[id,setId]=useState(0);
    const[name,setName]=useState("");
    const[price,setPrice]=useState("");
    const[editingProduct,setEditingProduct]=useState(null);
    const dataUpdate= (product)=> {
        setEditingProduct(product);
        console.log(editingProduct);
    }
    const token=localStorage.getItem('token');
    axios.interceptors.request.use(
        config=> {
            config.headers.authorization=`Bearer ${token}`;
            return config;
        },
        error=> {
            return Promise.reject(error);
        }

    );

    const [data,setData]=useState([])
    useEffect(()=> {
        axios.get("http://localhost:8080/products/fetchAll")
        .then(res=>setData(res.data))
        .catch(err=>console.log(err));
    },[])
  return (
    <div className='container'>
        <div className='mt-3'>
            <table className='table'>
                <thead>
                    <tr>
                        <th>Product ID</th>
                        <th>Product Name</th>
                        <th>Product Price</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((product,index)=> {
                            return <tr key={index}>
                                <td>{product.id}</td>
                                <td>{product.name}</td>
                                <td>{product.price}</td>
                                <td><button onClick={()=>dataUpdate(product)}>Update</button></td>

                            </tr>
                        })
                    }
                </tbody>
            </table>
            <input type="text"value={id} onChange={(e)=>{setId(e.target.value)}}/> <br/> <br/>
            <input type="text"value={name} onChange={(e)=>{setName(e.target.value)}}/> <br/> <br/>
            <input type="text"value={price} onChange={(e)=>{setPrice(e.target.value)}}/> <br/> <br/>


        </div>
    </div>
  )
}

export default UpdateProduct
