import React,{useEffect,useState} from 'react'
import axios from 'axios'

function UpdateProduct() {
    const[user,setUser]=useState([])
    const[id,setId]=useState(0);
    const[name,setName]=useState("");
    const[price,setPrice]=useState("");
    const[editingProduct,setEditingProduct]=useState(null);
    
    const token=localStorage.getItem('token');
    axios.interceptors.request.use(
        config=> {
            config.headers.authorization=`Bearer ${token}`;
            return config;
        },
        error=> {
            return Promise.reject(error);
        }

    );

    const [data,setData]=useState([])
    useEffect(()=> {
        axios.get("http://localhost:8080/products/fetchAll")
        .then(res=>setData(res.data))
        .catch(err=>console.log(err));
    },[])
    function selectUser(id) {
        let item=user[id-1];
        setId(item.id);
        setName(item.name);
        setPrice(item.price);
    }
    function getUsers() {
        fetch("http://localhost:8080/products/fetchAll").then((result)=> {
            result.json().then((resp)=> {
                setUser(resp)
                setName(resp[0].name)
                setPrice(resp[0].price)
            })
        })
    }
    function updateProduct() {
        let item={name,price}
        fetch('http://localhost:8080/product/update/${id}',{
            method:'PUT',
            headers: {
                'Accept':'application/json',
                'Content-Type':'application/json'

        },
        body:JSON.stringify(item)
    }).then((result)=> {
        result.JSON().then((resp)=> {
            console.warn(resp)
            getUsers()

        })
    })
}

  return (
    <div className='container'>
        <div className='mt-3'>
            <table className='table'>
                <thead>
                    <tr>
                        <th>Product ID</th>
                        <th>Product Name</th>
                        <th>Product Price</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((product,index)=> {
                            return <tr key={index}>
                                <td>{product.id}</td>
                                <td>{product.name}</td>
                                <td>{product.price}</td>
                                <td><button onClick={()=>selectUser(product.id)}>Update</button></td>

                            </tr>
                        })
                    }
                </tbody>
            </table>


        </div>
        <input type="text"value={name} onChange={(e)=>{setName(e.target.value)}}/> <br/> <br/>
            <input type="text"value={price} onChange={(e)=>{setPrice(e.target.value)}}/> <br/> <br/>
            <button onClick={updateProduct}>Update Product</button>
    </div>
  )
                }
            

export default UpdateProduct
